var cheerio = require('cheerio');
var log = console.log;

module.exports = linkTarget;

function linkTarget(str) {

	var $ = cheerio.load(str), href, target;

	$('a').each(function(i, e) {
		href = $(e).attr('href');
		target = $(e).attr('target');
		if (/^https?:\/\//.test(href) && !target) {
			$(e).attr('target', "_blank");
		}
	});

	// 返回 HTML
	return $.html();
}